INSERT INTO `page_contents` VALUES (1, 'Konten Header', 'header-homepage', 'photo/page-contents/content_header.jpg', '{\"1-text\":{\"type\":\"string\",\"name\":\"Judul Header\",\"content\":\"HIMPUNAN MAHASISWA TEKNOLOGI INFORMASI\"},\"2-text2\":{\"type\":\"text\",\"name\":\"Deskripsi Header\",\"content\":\"HIMATIF (Himpunan Mahasiswa Teknologi Informasi) adalah salah satu organisasi mahasiswa di Fakultas Ilmu Komputer, Universitas Jember yang memiliki tujuan pokok meningkatkan kualitas Sumber Daya Mahasiswa Teknologi Informasi.\"},\"3-photo\":{\"type\":\"image\",\"name\":\"Image\",\"content\":\"photo\\/page-contents\\/SSYS8pyDy7JxHXU6ZsRijFlraB6PzEcQMbO9pEgp.png\"},\"4-marquee_tag\":{\"type\":\"string\",\"name\":\"Marquee Info\",\"content\":\"Info\"},\"5-marquee_info\":{\"type\":\"mediumtext\",\"name\":\"Marquee Konten\",\"content\":\"<p>Open Registrasi Peserta Kompak Dibuka lagi loh !!!!. <a href=\\\"https:\\/\\/bit.ly\\/OpregPesertaKompak\\\"><b>Join Sekarang!<\\/b><\\/a><\\/p>\"}}', NULL, '2024-02-14 17:51:41');
INSERT INTO `page_contents` VALUES (2, 'Konten Header Tentang', 'header-about', 'photo/page-contents/content_header_about.jpg', '{\"1-text\":{\"type\":\"string\",\"name\":\"Judul Header\",\"content\":\"TENTANG HIMATIF\"},\"2-text2\":{\"type\":\"text\",\"name\":\"Deskripsi Header\",\"content\":\"HIMATIF (Himpunan Mahasiswa Teknologi Informasi) adalah Salah satu Organisasi Mahasiswa di Fakultas Ilmu Komputer Universitas Jember. Terbentuknya HIMATIF dirintis oleh 7 Orang Mahasiswa Teknologi Informasi pada tanggal 6 Agustus 2017.\"}}', NULL, NULL);
INSERT INTO `page_contents` VALUES (3, 'Konten Header Divisi & Pengurus', 'header-pengurus', 'photo/page-contents/content_header_pengurus.jpg', '{\"1-text\":{\"type\":\"string\",\"name\":\"Judul Header\",\"content\":\"DIVISI DAN PENGURUS\"},\"2-text2\":{\"type\":\"text\",\"name\":\"Deskripsi Header\",\"content\":\"HIMATIF memiliki struktur kepengurusan yang bertanggung jawab atas berbagai aspek dan kegiatan organisasi. Setiap divisi memiliki tugas pokok dan fungsi masing-masing untuk mencapai tujuan organisasi secara keseluruhan.\"}}', NULL, '2024-02-13 19:03:32');
INSERT INTO `page_contents` VALUES (4, 'Konten Header Proker', 'header-proker', 'photo/page-contents/content_header_proker.jpg', '{\"1-text\":{\"type\":\"string\",\"name\":\"Judul Header\",\"content\":\"PROKER HIMATIF\"},\"2-text2\":{\"type\":\"text\",\"name\":\"Deskripsi Header\",\"content\":\"Program kerja HIMATIF merupakan sejumlah kegiatan dan inisiatif yang ditujukan untuk meningkatkan kualitas pendidikan, pengembangan soft skill dan hard skill mahasiswa, serta memperkuat hubungan antar-mahasiswa dan dengan pihak-pihak terkait.\"}}', NULL, '2024-02-13 19:02:11');
